DROP TABLE IF EXISTS `mount_excretions`;
CREATE TABLE IF NOT EXISTS `mount_excretions` (
  `id` tinyint(3) unsigned NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `gameObjectEntry` mediumint(8) unsigned NOT NULL,
  PRIMARY KEY (`id`) USING BTREE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 ROW_FORMAT=COMPACT;


DROP TABLE IF EXISTS `mount_template`;
CREATE TABLE IF NOT EXISTS `mount_template` (
  `mountId` int(10) unsigned NOT NULL COMMENT 'Mount.db2 Id',
  `price` bigint(20) unsigned NOT NULL DEFAULT '0' COMMENT 'Price in copper',
  `fuelCapacity` float(12,4) NOT NULL DEFAULT '100.0000',
  `conditionCapacity` float(12,4) NOT NULL DEFAULT '100.0000',
  `consumptionPerMinute` float(12,4) NOT NULL DEFAULT '0.2000',
  `excretionId` tinyint(3) unsigned NOT NULL DEFAULT '1',
  PRIMARY KEY (`mountId`) USING BTREE,
  KEY `FK_mount_template_mount_excretions` (`excretionId`),
  CONSTRAINT `FK_mount_template_mount_excretions` FOREIGN KEY (`excretionId`) REFERENCES `mount_excretions` (`Id`) ON UPDATE CASCADE
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4;

