UPDATE `battlepay_display_info` SET `DisplayCardWidth`=1, `Name3`='Change the OOC name of your character.\n\n|cffffffffWarning:|r\n|cff02df04When changing the OOC name, the flag must be reassigned via TRP. Character-related add-on settings and data can also be reset.|r' WHERE `DisplayInfoId`=1;
UPDATE `battlepay_display_info_locales` SET `Name3`='Ändere den OOC-Namen deines Charakters.\n\n|cffffffffWarnung:|r\n|cff02df04Bei der Änderung des OOC-Namens muss das Flag über TRP neu zugewiesen werden. Ebenfalls können dabei Charakterbezogene AddOn Einstellungen und Daten zurückgesetzt werden.|r' WHERE `Id`=1 AND `Locale`='deDE';
